import React, { useRef, useEffect } from 'react';
import { jsPDF } from 'jspdf';

const Certificate = ({ name, course, date, hashCode }) => {
  const canvasRef = useRef(null);

  const handleDownload = () => {
    const canvas = canvasRef.current;
    const image = canvas.toDataURL('image/png');
    const pdf = new jsPDF('landscape');
    pdf.addImage(image, 'PNG', 0, 0, 297, 210); // Adjust dimensions as needed
    pdf.save(`${name}_certificate.pdf`);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const certificateImage = new Image();
    certificateImage.src = require('../../assets/certificate.png');
    certificateImage.onload = () => {
      ctx.drawImage(certificateImage, 0, 0, canvas.width, canvas.height);

      // Render dynamic text
      ctx.font = 'bold 20px Arial';
      ctx.fillStyle = 'black';
      ctx.fillText(`Name: ${name}`, 50, 100);
      ctx.fillText(`Course: ${course}`, 50, 150);
      ctx.fillText(`Date: ${date}`, 50, 200);
      ctx.fillText(`Hash Code: ${hashCode}`, 50, 250);
    };
  }, [name, course, date, hashCode]);

  return (
    <div>
      <canvas ref={canvasRef} width={800} height={600} />
      <button onClick={handleDownload}>Download Certificate</button>
    </div>
  );
};

export default Certificate;
