import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Certificate from '../other/Certificate';
import './home.css';

const Home = () => {
  const [certificates, setCertificates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCertificates = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Token not found');
        }

        const response = await axios.get("http://localhost:3001/user/home", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        const user = response.data;
        const userCertificates = user.certificates || [];
        setCertificates(userCertificates);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCertificates();
  }, []);


  return (
    <div className="container">
      <h1>Welcome {certificates.length > 0 ? certificates[0].name + "!" : "User!"}</h1>

      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}

      <div className="inner-container">
        <h2>Certificates</h2>
        {certificates.length === 0 && <p>No certificates found</p>}
        <ul>
          {certificates.map((certificate, index) => (
            <li key={index}>
              Course: {certificate.course}
              <Certificate
                name={certificate.name}
                prn={certificate.prn}
                course={certificate.course}
                date={certificate.date}
                hashCode={certificate.certificateHash}
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
