import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Certificate from '../other/Certificate';
import './home.css';

const Home = () => {
  const [certificates, setCertificates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCertificates = async () => {
      try {
        const response = await axios.get("http://localhost:3001/user/home");
        console.log("Response from server: ", response.data);
        setCertificates(response.data);
        setLoading(false); // Update loading state when certificates are fetched
      } catch (error) {
        console.error('Error fetching certificates: ', error);
        setError('Failed to fetch certificates'); // Set error message if fetching fails
        setLoading(false); // Update loading state even if fetching fails
      }
    }

    fetchCertificates();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Render a loading indicator while fetching certificates
  }

  if (error) {
    return <div>Error: {error}</div>; // Render an error message if fetching fails
  }

  return (
    <>
      <div className="container">
        <h1>Welcome {certificates.length > 0 ? certificates[0].name + "!" : "User!"}</h1>
        <div className="inner-container">
          <h2>Certificates</h2>
          <ul>
            {certificates && certificates.map((certificate, index) => (
              <li key={index}>
                Course: {certificate.course}
                <Certificate
                  name={certificate.name}
                  prn={certificate.prn}
                  course={certificate.course}
                  date={certificate.date}
                  hashCode={certificate.certificateHash}
                />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}

export default Home;
